def list_of_folders(path):
    lst_of_path =[]
    for folder_name in os.listdir(path):
        folder_path = os.path.join(path, folder_name)
        if os.path.isdir(folder_path):  # If it's a directory
            lst_of_path.append(folder_path)
    return lst_of_path


def copy_and_del(my_iter, dest):
    for item in my_iter:
        copy_directory(item, dest, False)


